# Encoding: utf-8# Date: 2019/5/30# Name: 仲玲from commen.log_method import Log_Methodfrom commen.read_config import Read_Configfrom commen.excel_util import Excerl_Methodfrom commen.broswer_setup import Broswer_setupfrom commen.http_request import HTTP_Requestimport unittestclass Login_Test(unittest.TestCase):	@classmethod	def setUpClass(cls):		cls.logger = Log_Method().get_logger()		# cls.driver = Broswer_setup(cls.logger).get_broswer()		cls.conf = Read_Config(cls.logger)		cls.http = HTTP_Request(cls.logger)		cls.url = cls.conf.read_config("login","url")		cls.data = int(cls.conf.read_config("login","data"))		cls.expect = int(cls.conf.read_config("login", "expected"))		cls.result = int(cls.conf.read_config("login", "result"))		cls.excel = Excerl_Method(cls.logger,"login")		cls.data_list = cls.excel.read_excel()	def test_login(self):		for i in range (len(self.data_list)):			responce = self.http.post_request(self.url,eval(self.data_list[i][self.data]))			if self.data_list[i][self.expect] in responce.decode():				self.excel.write_excel(i+2,"pass")			else:				self.excel.write_excel(i+2, "fail")if __name__ == '__main__':	unittest.main()																																				