# Encoding: utf-8# Date: 2019/5/30# Name: 仲玲from configparser import ConfigParserimport osclass Read_Config():	def __init__(self,logger):		self.logger = logger	def read_config(self,tagname,name):		file_name = os.path.dirname(os.path.abspath("."))+"\\config\\config.ini"		cf = ConfigParser()		cf.read(file_name)		res = cf.get(tagname,name)		return resif __name__ == '__main__':	from commen.log_method import Log_Method	logger = Log_Method().get_logger()	res = Read_Config(logger).read_config("server","ip")	print(res)